import random
from sympy import lcm

def generate_lcm_problem():
    # Generate a random number for the lcm target
    lcm_target = 60
    fixed_number = 20

    # Calculate the possible values of nu
    possible_nus = []
    for n in range(1, lcm_target + 1):
        nu = 3 * n
        if lcm(nu, fixed_number) == lcm_target:
            possible_nus.append(nu)

    # Calculate the sum of these values
    sum_of_nus = sum(possible_nus)

    # Sub-questions
    sub_questions = [
        {
            "question": "Why must nu be divisible by 3?",
            "answer": "Because 60 is divisible by 3, but 20 is not. Therefore, nu must contribute the factor of 3.",
            "explanation": "If lcm(nu, 20) = 60, then nu must be divisible by 3 since 20 is not divisible by 3."
        },
        {
            "question": "What equation must be satisfied for nu?",
            "answer": "lcm(3n, 20) = 60",
            "explanation": "We write nu as 3n, and thus we need to find n such that lcm(3n, 20) = 60."
        },
        {
            "question": "What must be true about n?",
            "answer": "n must be a divisor of 20",
            "explanation": "Since lcm(3n, 20) = 60, it follows that lcm(n, 20) = 20, which is true if and only if n is a divisor of 20."
        }
    ]

    return {
        "question": "What is the sum of all positive integers nu for which lcm(nu, 20) = 60?",
        "final_answer": sum_of_nus,
        "sub_questions": sub_questions
    }

import random

def generate_non_congruent_triangles():
    # Generate a random perimeter between 10 and 20
    perimeter = random.randint(10, 20)

    # Function to check if three sides can form a triangle
    def is_triangle(a, b, c):
        return a + b > c and a + c > b and b + c > a

    # Find all sets of integer sides that form a triangle with the given perimeter
    triangles = set()
    for a in range(1, perimeter // 2):
        for b in range(a, (perimeter - a) // 2 + 1):
            c = perimeter - a - b
            if c >= b and is_triangle(a, b, c):
                triangles.add(tuple(sorted((a, b, c))))

    # Sub-questions
    sub_questions = []
    sub_questions.append({
        "question": f"What is the perimeter of the triangle?",
        "answer": perimeter,
        "explanation": "The perimeter is the sum of the lengths of all three sides of the triangle."
    })
    sub_questions.append({
        "question": "List all possible sets of side lengths that form a triangle with the given perimeter.",
        "answer": list(triangles),
        "explanation": "Using the triangle inequality theorem, we find all sets of integer side lengths that can form a triangle with the given perimeter."
    })
    sub_questions.append({
        "question": "How many non-congruent triangles can be formed with the given perimeter?",
        "answer": len(triangles),
        "explanation": "The number of non-congruent triangles is the number of unique sets of side lengths that satisfy the triangle inequality theorem."
    })

    return {
        "question": f"How many non-congruent triangles with only integer side lengths have a perimeter of {perimeter} units?",
        "final_answer": len(triangles),
        "sub_questions": sub_questions
    }
